mapper.select:
SELECT MENU_NO, MENU_NM, MENU_PRC, MENU_DESC, MENU_STOCK_QTY, MENU_DISP_YN AS menu_Disp_Yn, MENU_IMG_NM AS FILE_NM FROM TB_MENU WHERE 1=1
and menu_no = #{menuNo}
and menu_nm like CONCAT('%', #{menuNm}, '%')

select ord_no, ord_dt, menu_no, menu_nm, ord_qty, ord_amt, ord_stat from tb_ord where 1=1
and ord_no = #{ordNo}
and ord_stat = #{ordStat}
and ord_dt >= date(subdate(now(), interval #{ordDt} DAY))
